class BookOfTheWeek(models.Model):
	name = models.CharField(max_length=1000, default='Harry Potter and the Half Blood Prince')
	photo = models.CharField(max_length=9000, default="data:image/jpeg;base64,")
	url = models.CharField(max_length=10000, default="http://www.passuneb.com/elibrary/ebooks/Harry%20Potter%20and%20The%20Half-Blood%20Prince.pdf")

	description = models.TextField(default='Победитель конкурса чтения')

	class Meta:
		verbose_name_plural = 'Книга недели/месяца/конкурса'

	def __str__(self):
		return self.name